INSERT INTO cursos (title, description, video_link)
VALUES
  ("HTML5", "Neste vídeo, você será introduzido ao HTML, a linguagem fundamental para a construção de páginas da web. Vamos explorar a estrutura básica de um documento HTML, incluindo a declaração do tipo de documento e as principais tags como <html>, <head>, e <body>. Você aprenderá a criar cabeçalhos, parágrafos, links e imagens, além de entender a importância da semântica no HTML. Ao final, você terá as habilidades necessárias para criar um esqueleto de página web funcional!", "https://link-do-video.com/html5"),
  ('CSS3', "Aprenda como transformar suas páginas web utilizando CSS! Neste vídeo, vamos abordar como aplicar estilos básicos a elementos HTML, explorando conceitos como seletores, propriedades e valores. Você verá como usar o CSS para modificar cores, fontes, tamanhos e espaçamentos, além de aprender sobre o box model e como criar layouts responsivos. No final deste vídeo, você será capaz de estilizar suas páginas de forma a criar experiências visuais atraentes e funcionais.Aprenda como transformar suas páginas web utilizando CSS! Neste vídeo, vamos abordar como aplicar estilos básicos a elementos HTML, explorando conceitos como seletores, propriedades e valores. Você verá como usar o CSS para modificar cores, fontes, tamanhos e espaçamentos, além de aprender sobre o box model e como criar layouts responsivos. No final deste vídeo, você será capaz de estilizar suas páginas de forma a criar experiências visuais atraentes e funcionais.", "https://link-do-video.com/css3"),
  ('JavaScript',  "Dê o primeiro passo na programação com JavaScript, a linguagem que traz interatividade para a web! Neste vídeo, você aprenderá sobre os conceitos básicos de JavaScript, incluindo variáveis, tipos de dados, operadores e estruturas de controle. Vamos explorar como criar funções e como trabalhar com o Document Object Model (DOM) para interagir com elementos da sua página. Ao final deste vídeo, você terá as ferramentas necessárias para adicionar dinamismo e funcionalidades aos seus projetos web.", "https://link-do-video.com/css3");